import React from 'react';

const Recipe = ({title,calories,totalWeight,servings,image,ingredients}) => {
    return(
        <div>
            <h1>{title}</h1>
            <p>{calories} kcal</p>
            <p>{totalWeight} g</p>
            <p>{servings} servings</p>
            <h2>Ingredients</h2>
            <p>
            <ol>
                {ingredients.map(ingredients => (
                    <li>{ingredients.text}</li>
                ))}
            </ol></p>
            <img src={image} alt=""/>
        </div>
    );
};

export default Recipe;
